import React, { useState, useRef, useEffect } from 'react';

function SunglassesOverlay({ faceImage, recommendedStyle }) {
  const [overlayPosition, setOverlayPosition] = useState({ top: 0, left: 0, width: 0 });
  const imageRef = useRef(null);
  
  // Map recommended styles to actual sunglasses images
  const sunglassesOptions = {
    'round': '/images/sunglasses-round.png',
    'square': '/images/sunglasses-square.png',
    'cat-eye': '/images/sunglasses-cat-eye.png',
    'aviator': '/images/sunglasses-aviator.png',
    'oversized': '/images/sunglasses-oversized.png'
  };
  
  // Position the sunglasses based on the face image dimensions
  useEffect(() => {
    if (imageRef.current) {
      const imgWidth = imageRef.current.offsetWidth;
      setOverlayPosition({
        top: imgWidth * 0.33, // Approximate eye position
        left: imgWidth * 0.15,
        width: imgWidth * 0.7
      });
    }
  }, [faceImage]);

  return (
    <div className="overlay-container" style={{ position: 'relative' }}>
      <img 
        ref={imageRef}
        src={faceImage} 
        alt="Face" 
        className="face-image"
      />
      {recommendedStyle && (
        <img
          src={sunglassesOptions[recommendedStyle] || sunglassesOptions['round']}
          alt="Sunglasses"
          className="sunglasses-overlay"
          style={{
            position: 'absolute',
            top: `${overlayPosition.top}px`,
            left: `${overlayPosition.left}px`,
            width: `${overlayPosition.width}px`,
            pointerEvents: 'none'
          }}
        />
      )}
    </div>
  );
}

export default SunglassesOverlay;
